import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function App() {

  const questions = [
    {
      questionText: 'When traveling, what type of restaurants do you like the best?',
      answerOptions: [
        { answerText: 'Have preparations done right from the start', isCorrect: '1.0' },
        { answerText: 'Have snacks carried along with you', isCorrect: '0.4' },
        { answerText: "Have your food cooked by your own hands", isCorrect: '0.7' },
        { answerText: "Have your food at any nearby food station", isCorrect: '0.1' },
        { answerText: "Have a journey especially for trying new taste", isCorrect: '0.3' },
        { answerText: "Have an experience on food and advise others", isCorrect: '0.2' },
        { answerText: "Have to take somebody else's advice for food", isCorrect: '0.1' }
      ]
    },
    {
      questionText: 'How do you decide on your next travel destination?',
      answerOptions: [
        { answerText: "Visit multiple websites and watch videos to know about the place", isCorrect: '1.0' },
        { answerText: "Know about the place from your friends and family", isCorrect: '1.0' },
        { answerText: "Try to pick a place as remote and close-to-nature as possible", isCorrect: '1.0' },
        { answerText: "Have equal preference for concrete and natural jungles", isCorrect: '1.0' },
        { answerText: "Try to visit places you have never been to before", isCorrect: '1.0' },
        { answerText: "Try to visit places you have been to know them better", isCorrect: '1.0' },
        { answerText: "Follow the directions of your fellow tour leader", isCorrect: '1.0' }
      ],
    },
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const [backColor, setbackColor] = useState(null);
  const [questionData, setQuestionData] = useState([]);

  const handleAnswerOptionClick = (isCorrect, i) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    setbackColor(i);
  };

  const moveNextQuestion = () => {
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
      setbackColor(null);
    } else {
      setShowScore(true);
    }
  }

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/users').done(function (data) {
      let quiz1 = data.myquestions[0].questions;
    })
  }, []);

  return (
    <div className='app'>
      {showScore ? (
        <div className='wrapper'>
          <div>
            <p className='userpoints'>You scored {score} out of {questions.length}</p>
          </div>
        </div>
      ) : (
          <div className='wrapper'>
            <div className='quiz'>
              <div className='quiz_header'>
                <div className='quiz_user'>
                  <h4>Welcome! <span className='name'>user_name</span></h4>
                </div>
                <div className='quiz_timer'>
                  <span className='time'>00:00</span>
                </div>
              </div>

              <div className='quiz_body'>
                {/* <h2><span className='questions'>Question {currentQuestion + 1}</span>/{questions.length}<br></br>
                  <span className='question-text'>{questions[currentQuestion].questionText}</span>
                </h2> */}
                <h2><span className='questions'>Question {currentQuestion + 1}</span>/{questions.length}<br></br>
                  <span className='question-text'>{questionData[currentQuestion].name === 'undefined' ? questionData[currentQuestion].name : 'props'}</span>
                </h2>
                <div className='option_group'>
                  {questions[currentQuestion].answerOptions.map((answerOption, i) => (
                    <button style={{ backgroundColor: backColor === i ? '#341f97' : '', color: backColor === i ? '#fff' : '' }} className='option' onClick={() => handleAnswerOptionClick(answerOption.isCorrect, i)}>{answerOption.answerText}</button>
                  ))}
                </div>
                <button className="btn-next" onClick={() => moveNextQuestion()}>Next Question</button>
              </div>
            </div>
          </div>
        )}
    </div>
  )
}
