import React, { useState } from 'react';
import './index.css';

export default function App() {
  const questions = [
    {
      questionText: 'When traveling, what type of restaurants do you like the best?',
      answerOptions: [
        { answerText: 'Have preparations done right from the start', isCorrect: '10' },
        { answerText: 'Have snacks carried along with you', isCorrect: '4' },
        { answerText: "Have your food cooked by your own hands", isCorrect: '7' }
      ]
    },
    {
      questionText: 'How do you decide on your next travel destination?',
      answerOptions: [
        { answerText: 'Visit multiple websites and watch videos to know about the place', isCorrect: '10' },
        { answerText: 'Know about the place from your friends and family', isCorrect: '4' },
        { answerText: "ry to pick a place as remote and close-to-nature as possible", isCorrect: '5' }
      ],
    },
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  return (
    <div className='app'>
      {showScore ? (
        <div className='wrapper'>
          <div>
            <p className='userpoints'>You scored {score} out of {questions.length}</p>
          </div>
        </div>
      ) : (
          <div className='wrapper'>
            <div className='quiz'>
              <div className='quiz_header'>
                <div className='quiz_user'>
                  <h4>Welcome! <span className='name'>user_name</span></h4>
                </div>
                <div className="quiz_timer">
                  <span className="time">00:00</span>
                </div>
              </div>

              <div className='quiz_body'>
                <span className='questions'>Question {currentQuestion + 1}</span>/{questions.length}
              </div>
              <div className='question-text'>{questions[currentQuestion].questionText}</div>
            </div>
            <div className='option_group'>
              {questions[currentQuestion].answerOptions.map((answerOption) => (
                <button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
              ))}
            </div>
          </div>
        )}
    </div>
  )
}
