import React, { useState } from 'react';

export default function App() {
  const questions = [
    {
      questionText: 'When traveling, what type of restaurants do you like the best?',
      answerOptions: [
        { answerText: 'Have preparations done right from the start', isPoint: '10' },
        { answerText: 'Have snacks carried along with you', isPoint: '4' },
        { answerOptions: "Have your food cooked by your own hands", isPoint: '7' }
      ]
    },
    {
      questionText: 'How do you decide on your next travel destination?',
      answerOptions: [
        { answerText: 'Visit multiple websites and watch videos to know about the place', isPoint: '10' },
        { answerText: 'Know about the place from your friends and family', isPoint: '4' },
        { answerOptions: "ry to pick a place as remote and close-to-nature as possible", isPoint: '5' }
      ],
    },
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  return (
    <div className='app'>
      {showScore ? (
        <div className='score-section'>
          You scored {score} out of {questions.length}
        </div>
      ) : (
          <>
            <div className="question-section">
              <div className='question-count'>

              </div>
            </div>
          </>
        )}
    </div>
  )
}
